import React, { Component } from "react";

class ConstructorSetState extends Component {
    // 
    state = {
      name: "peter",
    };

    constructor() {
      super();
      // 
      this.state = {
        name: "John",
      };
      console.log("hello constructor");
    }

    render() {
      return (
        <div className="const_state">
          <h1>hello constructor state</h1>
          {this.state.name}
        </div>
      );
    }
  }

  export default ConstructorSetState;
